import React from 'react';
import logo from "./logo.svg";
import Form from './Form';
import './style.css';
import Axios from "axios";


export default class App extends React.Component {
    constructor(props) {
      super(props);

      this.getCountryData = this.getCountryData.bind(this);
    }
  state = {
    confirmed: 0,
    recovered: 0,
    deaths: 0,
    countries: []
  }

  state = {
    firstName: "",
    lastName: "",
    username: "",
    email: "",
    password: ""
  };

  change = e => {
    this.props.onChange({ [e.target.name]: e.target.value });
    this.setState({
      [e.target.name]: e.target.value
    });
  };

  onSubmit = e => {
    e.preventDefault();
    // this.props.onSubmit(this.state);
    this.setState({
      firstName: "",
      lastName: "",
      username: "",
      email: "",
      password: ""
    });
    this.props.onChange({
      firstName: "",
      lastName: "",
      username: "",
      email: "",
      password: ""
    });
  };



  componentDidMount() {
    this.getData();
  }

  async getData() {
    const resApi = await Axios.get("https://covid19.mathdro.id/api");
    const resCountries = await Axios.get("https://covid19.mathdro.id/api/countries");
    const countries = Object.keys(resCountries.data.countries);

    this.setState({
        confirmed: resApi.data.confirmed.value,
        recovered: resApi.data.recovered.value,
        deaths: resApi.data.deaths.value,
        countries: countries,
    });
  }

async getCountryData(e) {
  if (e.target.value === "Worldwide") {
    return this.getData();
  }
  try {
   const res = await Axios.get(`https://covid19.mathdro.id/api/countries/${e.target.value}`);
    this.setState({
        confirmed: res.data.confirmed.value,
        recovered: res.data.recovered.value,
        deaths: res.data.deaths.value,
});
  }
  catch (err) {
    if (err.response.status === 404)
    this.setState({
      confirmed: "No data available",
      recovered: "No data available",
      deaths: "No data available",
  });
}
  }

renderCountryOptions() {
      return this.state.countries.map((country, i) => {
        return <option key={i}>{country}</option>
      });
}

  render() {
    return (
      <> <div className="box container">
      <h1>COVID-19 Update</h1>

<select className="dropdown" onChange={this.getCountryData}>
  <option>Worldwide</option>
  {this.renderCountryOptions()}
</select>
    <div className="flex">
      <div className="box confirmed">
        <h3>Confirmed cases</h3>
      <h4>{this.state.confirmed}</h4>
      </div>
      <div className="box recovered">
      <h3>Recovered cases</h3>
      <h4>{this.state.recovered}</h4>
      </div>
      <div className="box deaths">
      <h3>Deaths cases</h3>
      <h4>{this.state.deaths}</h4>
      </div>
      </div>


    </div>


    <form>
        <input
          name="firstName"
          placeholder="First name"
          value={this.state.firstName}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="lastName"
          placeholder="Last name"
          value={this.state.lastName}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="username"
          placeholder="Username"
          value={this.state.username}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="email"
          placeholder="Email"
          value={this.state.email}
          onChange={e => this.change(e)}
        />
        <br />
        <input
          name="password"
          type="password"
          placeholder="Password"
          value={this.state.password}
          onChange={e => this.change(e)}
        />
        <br />
        <button onClick={e => this.onSubmit(e)}>Submit</button>
      </form>
</>
    );

  }
}
